import { reduxForm, Field } from 'redux-form';
import { compose, withProps, lifecycle } from 'recompose';
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from 'react-google-maps';
import './feedback.css';
const MyMapComponent = compose(
    withProps({
        googleMapURL:
            'https://maps.googleapis.com/maps/api/js?key=AIzaSyCYSleVFeEf3RR8NlBy2_PzHECzPFFdEP0&libraries=geometry,drawing,places',
        loadingElement: <div style={{ height: `100%` }} />,
        containerElement: <div style={{ height: `400px` }} />,
        mapElement: <div style={{ height: `100%` }} />,
    }),
    lifecycle({
        componentWillMount() {
            const refs = {};

            this.setState({
                position: null,
                onMarkerMounted: ref => {
                    refs.marker = ref;
                },

                onPositionChanged: () => {
                    const position = refs.marker.getPosition();
                    console.log(position.toString());
                },
            });
        },
    }),
    withScriptjs,
    withGoogleMap
)(props => (
    <GoogleMap defaultZoom={8} defaultCenter={{ lat: -34.397, lng: 150.644 }}>
        {props.isMarkerShown && (
            <Marker
                position={{ lat: -34.397, lng: 150.644 }}
                draggable={true}
                ref={props.onMarkerMounted}
                onPositionChanged={props.onPositionChanged}
            />
        )}
    </GoogleMap>
));
var isValid=true;
var alertStr="";
function fieldChanges(event,key){
    let text = event.target.value;
    alertStr="";
    switch(key){
      case 'FULL_NAME':
        if(text.length>255)
        {
          alertStr="you have exceeding 255 character for First Name";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      case 'D_O_B':
        if(new Date(text)>new Date())
        {
          alertStr="You should select date before tadays date";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      case 'PINCODE':
        if(text.length>6)
        {
          alertStr="you have exceeding limit for PINCODE";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      case 'EMAIL':
        isValid=true;        
        break;
      case 'P_MOBILE':
        if(text.length>13)
        {
          alertStr="you have exceeding limit for primary Mobile number";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      case 'O_MOBILE':
        if(text.length>13)
        {
          alertStr="you have exceeding limit for Other Mobile number";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      case 'ADDRESS':
        if(text.length>200)
        {
          alertStr="you have exceeding 200 character for addess";
          isValid=false;
        }
        else{
          isValid=true;
        }
        break;
      default:
        console.log("do nothing");
        break;
    }
    if(!isValid)
    alert(alertStr);
}
function myHandleSubmit(formValues){
    alert((isValid)?"Submited Sucessfully "+formValues["first_name"]:alertStr);
}
function PersonalInfo(props){
    console.log(props);
    const {handleSubmit}=props;
      return (
        <div className="feedbackContainer">
          <form className="form" onSubmit={handleSubmit(formValues=>myHandleSubmit(formValues))}>
            <h1>Personal Details</h1>
            <label>Full Name</label>
            <Field onChange={(event)=>fieldChanges(event,'FULL_NAME')} type="text" name="full_name" component="input"></Field>
            <label>Date Of Birth</label>
            <Field onChange={(event)=>fieldChanges(event,'D_O_B')} type="date" name="dob" component="input"></Field>
            <label>Pincode</label>
            <Field onChange={(event)=>fieldChanges(event,'PINCODE')} type="number" name="pincode" component="input"></Field>
            <label>Gender</label>
            <Field onChange={(event)=>fieldChanges(event,'GENDER')} name="select" component="select" >
            <option>Select</option><option>Male</option><option>Femail</option><option>Other</option>
            </Field>
            <label>Email Id</label>
            <Field onChange={(event)=>fieldChanges(event,'EMAIL')} type="email" name="email" component="input"></Field>
            <label>Primary Mobile No</label>
            <Field onChange={(event)=>fieldChanges(event,'P_MOBILE')} type="number" name="pmobile" component="input"></Field>
            <label>Other Mobile No</label>
            <Field onChange={(event)=>fieldChanges(event,'O_MOBILE')} type="number" name="omobile" component="input"></Field>
            <label>Address</label>
            <Field onChange={(event)=>fieldChanges(event,'ADDRESS')} type="text" name="address" component="input"></Field>
            <MyMapComponent  />
            <button type="submit">Submit</button>
          </form>
        </div>
    )}
const Personal = reduxForm({
    form:'personal'
})(PersonalInfo);
export default Personal;
