import fire from '../fire.js';
import {reduxForm,Field} from 'redux-form';
import HomePage from './homePage';
import {Route,Switch,useRouteMatch,Link} from 'react-router-dom';
//import {useState} from 'react';
function signIn(values){
    fire.auth().signInWithEmailAndPassword(values['email'], values['password'])
    .then((userCredential) => {
        console.log(userCredential.user.email)
    })
    .catch((error) => {
    var errorMessage = error.message;
    alert(errorMessage);
  });
}
fire.auth().signOut().then(() => {
    // Sign-out successful.
  }).catch((error) => {
    // An error happened.
  });
function Login(props){
    //const [isAuthorised,setAuthorisation] = useState(false);
    fire.auth().onAuthStateChanged((user) => {
        if (user) {
            document.getElementById("loginSection").style.visibility = "hidden"; 
            document.getElementById("authorisedUser").style.visibility = "visible"; 
        } else {
            document.getElementById("loginSection").style.visibility = "visible"; 
            document.getElementById("authorisedUser").style.visibility = "hidden"; 
        }
    });
    const {handleSubmit}=props;
    return(<div>
        <div id='loginSection'className="feedbackContainer">
            <form className="form" onSubmit={handleSubmit(formValues=>signIn(formValues))}>
                <h2>Email Id</h2>
                <Field type="email" name="email" component="input"/>
                <h2>Password</h2>
                <Field type="password" name="password" component="input"/>
                <div id="toggle">
                    <button type="submit">SignIn</button>
                </div>
                <Link to='/sign-up'><button className="gotoButton" >Go To Sign-Up Page</button></Link>
            </form>
        </div> 
        <div id="authorisedUser" >
            <HomePage/>
        </div>
        </div>
    )
}
var LoginPage = reduxForm({
        form:'login'
    })(Login)
export default LoginPage;
