import fire from '../fire.js';
import {reduxForm,Field} from 'redux-form';
import {Link} from "react-router-dom";
function signIn(values){
    fire.auth().signInWithEmailAndPassword(values['email'], values['password'])
    .then((userCredential) => {
    var user = userCredential.user;
    alert('user signed in successfully');
    })
    .catch((error) => {
    var errorMessage = error.message;
    alert(errorMessage);
  });
}
function afterSignin(){
    fire.auth().onAuthStateChanged((user) => {
        if (user) {
          // User is signed in, see docs for a list of available properties
          // https://firebase.google.com/docs/reference/js/firebase.User
          var uid = user.uid;
          // ...
        } else {
          // User is signed out
          // ...
        }
      });
}

function Login(props){
    const {handleSubmit}=props;
    return(
        <div className="feedbackContainer">
            <form className="form" onSubmit={handleSubmit(formValues=>signIn(formValues))}>
                <h2>Email Id</h2>
                <Field type="email" name="email" component="input"/>
                <h2>Password</h2>
                <Field type="password" name="password" component="input"/>
                <button type="submit">Sign In</button>
                <Link to='/sign-up'><button className="gotoButton">Go To Sign-Up page</button></Link>
            </form>
        </div>
    )
}
var LoginPage = reduxForm({
        form:'login'
    })(Login)
export default LoginPage;
