import './App.css';
import { reduxForm, Field } from 'redux-form';
function fieldChanges(event,key){
  let text = event.target.value;
  switch(key){
    case 'FIRST_NAME':
      if(text.length>64)
      {
        alert("you have exceeding 64 character for First Name");
        text = text.substring(0,64);
        console.log(text.length);
      }
      break;
    case 'LAST_NAME':
      if(text.length>64)
      {
        alert("you have exceeding 64 character for Last Name");
        text = text.substring(0,64);
        console.log(text.length);
      }
      break;
    case 'EMAIL':

      break;
    case 'MOBILE':
      if(text.length>13)
      {
        alert("you have exceeding limit for Mobile number");
        text = text.substring(0,13);
        console.log(text.length);
      }
      break;
    case 'COMMENT':
      if(text.length>200)
      {
        alert("you have exceeding 200 character for Comment");
        text = text.substring(0,200);
        console.log(text.length);
      }
      break;
    default:
      console.log("do nothing");
  }
}
function App(props){
  console.log(props);
  const {handleSubmit}=props;
    return (
      <div className="App">
        <form className="form" onSubmit={handleSubmit((formValues)=>{alert("Thanks for feedback "+formValues["first_name"])})}>
          <h1>Feedback</h1>
          <Field type="text" value="pawan" component="input" name="demo"></Field>
          <label>First Name</label>
          <Field onChange={(event)=>fieldChanges(event,'FIRST_NAME')} type="text" name="first_name" component="input"></Field>
          <label>Last Name</label>
          <Field onChange={(event)=>fieldChanges(event,'LAST_NAME')} type="text" name="last_name" component="input"></Field>
          <label>Email Id</label>
          <Field onChange={(event)=>fieldChanges(event,'EMAIL')} type="email" name="email" component="input"></Field>
          <label>Mobile No</label>
          <Field onChange={(event)=>fieldChanges(event,'MOBILE')} type="number" name="mobile" component="input"></Field>
          <label>How did you hear about us?</label>
          <Field onChange={(event)=>fieldChanges(event,'HOW_DID_KNOW')} name="select" component="select" >
            <option>Select</option><option>By social media</option><option>By internet</option><option>By news paper</option>
          </Field>
          <label>Comment</label>
          <Field onChange={(event)=>fieldChanges(event,'COMMENT')} type="text" name="comment" component="input"></Field>
          <button type="submit">Submit</button>
        </form>
        </div>
    );
}
let Application = reduxForm({
  // a unique name for the form
  form: 'myForm'
})(App)
export default Application;
