import './App.css';
import { reduxForm, Field } from 'redux-form';
import {Component} from 'react';
import {connect} from 'react-redux'

class App extends Component {
  constructor(props)
  {
    super(props)
    this.handleSubmit = ()=>
    {
      this.clicked =  this.props.count;
    }
    this.handleOver = ()=>
    {
      this.over =  this.props.count;
    }
  }

  render(){
    return (
      <div className="App">
        <button onClick={this.handleSubmit}>Clicked {this.clicked} Times</button>
        <label onMouseOver={this.handleOver}>Rollover {this.over} Times</label>
      </div>
    );
  }
}

App = reduxForm({
  // a unique name for the form
  form: 'contact'
})(App)
export default App;
