import './App.css';
import { reduxForm, Field } from 'redux-form';

function App(props){
  console.log(props);
  const {handleSubmit}=props;
    return (
      <div className="App">
        <form className="form" onSubmit={handleSubmit((formValues)=>alert("Submited Data: "+formValues['first']+" "+formValues['second']))}>
          <h1>Feedback</h1>
          <label>First Name</label>
          <Field type="text" name="first_name" component="input"></Field>
          <label>Last Name</label>
          <Field type="text" name="last_name" component="input"></Field>
          <label>Email Id</label>
          <Field type="email" name="email" component="input"></Field>
          <label>Mobile No</label>
          <Field type="number" name="mobile" component="input"></Field>
          <label>How did you hear about us?</label>
          <Field name="select" component="select">
            <option>Select</option><option>by social media</option><option>by internet</option><option>by news paper</option>
          </Field>
          <label>Comment</label>
          <Field type="text" name="comment" component="input"></Field>
          <button type="submit">Submit</button>
        </form>
        </div>
    );
}
let Application = reduxForm({
  // a unique name for the form
  form: 'myForm'
})(App)
export default Application;
